import React, { Component } from 'react';
import { Link, NavLink } from 'react-router-dom';
import TaskList from '../components/TaskList';
import NewList from './NewList';
import './Dashboard.scss';
import axios from 'axios';

class Dashboard extends Component {

    state = {
        lists: [],
        tasks: [],
        currentListId: null
    }

    componentDidMount() {
        axios.get("http://localhost:3001/dashboard").then(res => {
            this.setState({
                lists: res.data,
            });
            // default 1st list with its tasks
            if (this.state.lists.length) {
                this.handleList(this.state.lists[0]._id);
            }
        });
    }

    handleList = (listId) => {
        axios.get(`http://localhost:3001/dashboard/${listId}/tasks`).then(res => {
            this.setState({
                tasks: res.data,
                currentListId: listId
            });
            console.log(this.state);
        });
    }

    render() {
        const { lists, tasks } = this.state;
        const listLists = !lists.length ? (<div>No lists added</div>) : (
            lists.map(list => {
                return (
                    <div onClick={() => this.handleList(list._id)} className="list-menu-item" key={list._id}>
                        <NavLink to={`dashboard/${list._id}`}>
                            <p>{list.title}</p>
                        </NavLink>
                    </div>
                )
            })
        )

        return (
            <div className="centered-content">
                <div className="task-manager-container">

                    {/* leftside - name of lists */}
                    <div className="sidebar has-background-white">
                        <h1 className="title has-text-primary">Lists ({lists.length})</h1>
                        <div className="list-menu">{listLists}</div>
                        <Link to="/new-list">
                            <button className="button is-primary has-text-white">+ New List</button>
                        </Link>


                    </div>

                    {/* rightside - list with all its tasks */}
                    <div className="tasks-list-container has-background-light">
                        <div className="level">

                            <div className="level-left">
                                <div className="level-item">
                                    <h1 className="title has-text-primary">Tasks</h1>
                                </div>
                            </div>
                            <div className="level-right">
                                <div className="level-item">

                                    {this.state.tasks.length === 0 ?
                                        <Link to={`/edit-list/${this.state.currentListId}`}>
                                            <span className="list-settings-icon"><i className="fas fa-wrench"></i></span>
                                        </Link>
                                        : <Link to='/'><span className="list-settings-icon"><i className="fas fa-wrench"></i></span></Link>
                                    }

                                </div>
                            </div>
                        </div>

                        <TaskList tasks={tasks} />
                    </div>

                </div>
            </div>
        )
    }
}

export default Dashboard
