import React from 'react';
import { Link } from 'react-router-dom'
import './TaskItem.scss';

const TaskItem = (props) => {
    const { task } = props
    let tag;
    if (task.status === 'bug') tag = 'is-danger';
    if (task.status === 'new') tag = 'is-dark';
    if (task.status === 'closed') tag = '';

    const date = new Date(task.date);
    const now = new Date();
    const hoursPassed = new Date(now - date).getMinutes();
    console.log(hoursPassed);

    return (
        <Link to={`/dashboard/${task._listId}/tasks/${task._id}`}>
            <div className="task level">
                <div className="level-left">
                    <div className="level-item">
                        <span className={tag + " tag m-r-md"}>{task.status}</span>
                        {task.title}
                    </div>
                </div>
                <div className="level-right">
                    <div className="level-item">
                        <h3 className="time">{hoursPassed} min ago</h3>
                    </div>
                </div>

            </div>
        </Link>
    )
}

export default TaskItem
