import React, { useContext, useEffect, useState } from 'react';
import UserContext from '../../UserContext';
import axios from 'axios';
import './Home.scss';

const Home = (props) => {
    const { isAuth } = useContext(UserContext);
    const [user, setUser] = useState({});

    useEffect(() => {
        axios.get('http://localhost:3001/')
            .then(res => {
                return res.json();
            }).then(resJson => {
                setUser({
                    isAuth: resJson.data.authenticated
                });
                console.log(user);
            }).catch(err => {
                setUser({
                    authenticated: false,
                    error: "Failed to authenticate user"
                });
            });
    });

    return (
        <section className="hero tinted-image is-fullheight-with-navbar">
            <div className="hero-body">
                <div className="container has-text-centered">

                    <h1>{isAuth}</h1>

                    <p className=" has-text-white is-size-1">Welcome To Manager</p>
                    <p className="p-b-lg has-text-grey-light is-size-4">Manager helps teams collaborate easlly. Try us!</p>
                    <button className="button is-info">
                        <span className="icon">
                            <i className="fas fa-bars"></i>
                        </span>
                        <strong><span>Create New List</span></strong>
                    </button>
                </div>
            </div>
        </section>

    )
}

export default Home
