import React, { Component } from 'react';
import TaskList from '../components/TaskList';
import './Dashboard.scss';
import axios from 'axios';

class Dashboard extends Component {

    componentDidMount() {
    }

    onClickHandler = (e) => {
        // e.preventDefualt()
        const url = 'http://localhost:3001/lists'
        const lists = axios.get(url)
            .then(res => console.log(res.data));
    }

    render() {

        const lists = axios.get("http://localhost:3001/lists").then(res => {
            res.data.map(list =>
                <a className="list-menu-item">
                    <p>{list.title}</p>
                </a>
            );
        });

        return (
            <div className="centered-content m-t-xl">
                <div className="task-manager-container">

                    {/* leftside - name of lists */}
                    <div className="sidebar has-background-white">
                        <h1 className="title has-text-primary">Lists</h1>
                        <div className="list-menu">

                            <div>{lists}</div>
                            {/* <a className="list-menu-item is-active">
                                <p>List One</p>
                            </a>
                            <a className="list-menu-item">
                                <p>List One</p>
                            </a>
                            <a className="list-menu-item">
                                <p>List One</p>
                            </a>
                            <a className="list-menu-item">
                                <p>List One</p>
                            </a> */}

                        </div>

                        <button onClick={this.onClickHandler} className="button is-primary has-text-white">+ New List</button>

                    </div>

                    {/* rightside - list with all its tasks */}
                    <div className="tasks-list-container has-background-light">
                        <TaskList />
                    </div>


                </div>
            </div>
        )
    }
}

export default Dashboard
