import React from 'react';
import { Link } from 'react-router-dom'
import './TaskItem.scss';

const TaskItem = (props) => {
    const { task } = props
    console.log(task);
    return (
        // /lists/:listId/tasks/:taskId
        <Link to={`/dashboard/${task._listId}/tasks/${task._id}`}>
            <div className="task">
                <p>{task.title}</p>
            </div>
        </Link>
    )
}

export default TaskItem
