import React, { Component } from 'react';
import TaskList from '../components/TaskList';
import './Dashboard.scss';
import axios from 'axios';

class Dashboard extends Component {

    state = {
        lists: [],
        tasks: [],
        showModal: false
    }

    componentDidMount() {
        axios.get("http://localhost:3001/lists").then(res => {
            this.setState({
                lists: res.data
            });
            // default 1st list with its tasks
            if (this.state.lists.length) {
                this.listHandler(this.state.lists[0]._id);
            }
        });
    }

    listHandler = (listId) => {
        axios.get(`http://localhost:3001/lists/${listId}/tasks`).then(res => {
            this.setState({
                tasks: res.data
            });
            console.log(res.data);
        });
    }

    addListHandler = () => {



        // axios.post('http://localhost:3001/lists').then(res => {
        //     console.log(res);
        // })
    }

    render() {
        const { lists, tasks } = this.state;
        const listLists = !lists.length ? (<div>No lists added</div>) : (
            lists.map(list => {
                return (
                    <div onClick={() => this.listHandler(list._id)} className="list-menu-item" key={list._id}>
                        <p>{list.title}</p>
                    </div>
                )
            })
        )

        return (

            <div className="centered-content m-t-xl">
                <div className="task-manager-container">

                    {/* leftside - name of lists */}
                    <div className="sidebar has-background-white">
                        <h1 className="title has-text-primary">Lists</h1>
                        <div className="list-menu">{listLists}</div>

                        <button onClick={this.addListHandler} className="button is-primary has-text-white">+ New List</button>

                    </div>

                    {/* rightside - list with all its tasks */}
                    <div className="tasks-list-container has-background-light">
                        <h1 className="title has-text-primary">Tasks</h1>
                        <TaskList tasks={tasks} />
                    </div>

                </div>
            </div>
        )
    }
}

export default Dashboard
