import React, { useState, useEffect } from 'react';
import TaskList from './TaskList';
import axios from 'axios';

const SearchTask = (props) => {

    const [tasks, setTasks] = useState([]);

    useEffect(() => { setTasks(props.tasks) }, [props.tasks])

    const handleChange = (e) => {
        let filteredTasks = tasks.filter(task => {
            return task.title.startsWith(e.target.value);
        });
        console.log("input " + e.target.value);
        console.log("filtered length: " + filteredTasks.length);
        if (e.target.value != '') setTasks(filteredTasks);
    }

    return (
        <>
            <div className="field has-addons">
                <div className="control">
                    <input onChange={handleChange} className="input" type="text" placeholder="find task" />
                </div>
            </div>
            <TaskList tasks={tasks} />
        </>
    )
}

export default SearchTask
