import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import axios from 'axios';

class EditTask extends Component {
    state = {
        title: ""
    }

    handleSubmit = (listId, taskId) => {
        console.log(listId);
        // e.preventDefault();
        axios.patch(`http://localhost:3001/dashboard/${listId}/tasks/${taskId}`, { title: this.state.title })
            .then(res => {
                console.log(res)
            }, err => {
                console.log(err)
            });
        this.props.history.push('/dashboard');
    }

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }

    render() {

        const { listId, taskId } = this.props.match.params;

        return (
            <div className="centered-content">
                <div className="modal-box">
                    <h1 className="title">task: {this.state.title}</h1>
                    <form onSubmit={() => this.handleSubmit(listId, taskId)}>
                        <div className="field">
                            <input onChange={this.handleChange} id="title" className="input" type="text" placeholder="list name" />
                        </div>

                        <div className="field buttons is-right">
                            <input type="submit" className="button is-info" placeholder="Add" />
                            <button className="button">Cancel</button>
                        </div>
                    </form>
                </div>
            </div>
        )
    }
}

export default withRouter(EditTask)
