import React, { useEffect, useState } from 'react'
import queryString from 'query-string';

const Profile = (props) => {

    const [user, setUser] = useState(window.localStorage.getItem('jwt'));

    // const currentUser = window.localStorage.getItem('jwt') ? "Logged In" : "logged Out"

    useEffect(() => {
        const query = queryString.parse(props.location.search);
        if (query.token) {
            window.localStorage.setItem("jwt", query.token);
            setUser(window.localStorage.getItem('jwt'));
        }
    });

    const online = user ? (<h1>logged in</h1>) : (<h1>logged out</h1>)


    return (
        <h1>{online}</h1>
    )
}

export default Profile
