import React from 'react';
import { Link } from 'react-router-dom'
import './TaskItem.scss';

const TaskItem = (props) => {
    const { task } = props
    let tag;
    if (task.status === 'bug') tag = 'is-danger';
    if (task.status === 'open') tag = 'is-dark';
    if (task.status === 'closed') tag = '';

    const miliseconds = new Date() - new Date(task.date).getTime();
    console.log(miliseconds / 1000);

    const date = new Date(task.date).getTime();
    console.log(date)
    console.log(Math.round(date / (1000 * 60)));
    // const now = new Date().getDate();
    // console.log("date:" + date);
    // console.log("now - date: " + new Date(now - date))
    // const hoursPassed = new Date(now - date).toLocaleString();

    return (
        <Link to={`/dashboard/${task._listId}/tasks/${task._id}`}>
            <div className="task level">
                <div className="level-left">
                    <div className="level-item">
                        <span className={tag + " tag m-r-md"}>{task.status}</span>
                        {task.title}
                    </div>
                </div>
                <div className="level-right">
                    <div className="level-item">
                        <h3 className="time">{date} min ago</h3>
                    </div>
                </div>

            </div>
        </Link>
    )
}

export default TaskItem
