import React from 'react';
import { Link } from 'react-router-dom'
import './TaskItem.scss';

const TaskItem = (props) => {
    const { task } = props
    return (
        <Link to={`/dashboard/${task._listId}/tasks/${task._id}`}>
            <div className="task">
                <span>{task.status}</span>
                <p>{task.title}</p>
            </div>
        </Link>
    )
}

export default TaskItem
