import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import axios from 'axios';

class EditTask extends Component {
    state = {
        title: ""
    }

    componentDidMount() {
        const { listId, taskId } = this.props.match.params;

        axios.get(`http://localhost:3001/dashboard/${listId}/tasks/${taskId}`).then(res => {
            this.setState({
                title: res.data.title
            })

        })
    }

    handleSubmit = (e, listId, taskId) => {
        e.preventDefault();
        axios.patch(`http://localhost:3001/dashboard/${listId}/tasks/${taskId}`, { title: this.state.title })
            .then(res => {
                console.log(res)
            }, err => {
                console.log(err)
            });
        this.props.history.push('/dashboard');
    }

    handleChange = (e) => {
        console.log(this.props);
        this.setState({
            [e.target.id]: e.target.value
        })
    }

    render() {

        const { listId, taskId } = this.props.match.params;

        return (
            <div className="centered-content">
                <div className="modal-box">
                    <h1 className="title">task:</h1>
                    <form onSubmit={e => this.handleSubmit(e, listId, taskId)}>
                        <div className="field">
                            <input onChange={this.handleChange} id="title" className="input" type="text" placeholder="list name" />
                        </div>

                        <div className="field buttons is-right">
                            <input type="submit" className="button is-info" placeholder="Add" />
                            <Link to="/dashboard"><button className="button">Cancel</button></Link>
                        </div>
                    </form>
                </div>
            </div>
        )
    }
}

export default withRouter(EditTask)
