import React from 'react';
import TaskItem from './TaskItem';
import './TaskList.scss';

const TaskList = (props) => {
    const { tasks } = this.props;
    const taskList = !tasks.length ? (<p>no tasks</p>) : (
        tasks.map(task => {
            return (
                <div id={task._id}>
                    <a className="list-menu-item is-active" href="#">
                        <TaskItem task={task} />
                    </a>
                </div>
            )
        })
    )
    return (
        <div>{taskList}</div>
    )
}

export default TaskList
