import React, { useState, useEffect } from 'react'
import axios from 'axios';

const CreateTask = () => {

    const handleSubmit = (e, taskId) => {
        e.preventDefault();
        axios.post(`/dashboard/${listId}/tasks`, { title: taskId })
            .then(console.log('new task added!'));
    }


    return (
        <div className="centered-content">
            <div className="modal-box">
                <h1 className="title">Create New Task</h1>
                <form onSubmit={e => handleSubmit(e, taskId)}>
                    <div className="field">
                        <input onChange={this.handleChange} id="title" className="input" type="text" placeholder="give the task a name" />
                    </div>

                    <div className="field buttons is-right">
                        <input type="submit" className="button is-info" placeholder="Add Task" />
                        <Link to="/dashboard"><button className="button">Cancel</button></Link>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default CreateTask
