                    <div className="level-item">
                        <span className={tag + " tag m-r-md"}>{task.status}</span>
                        {task.title}
                    </div>
                </div>
                <div className="level-right">
                    <div className="level-item">
                        <h3 className="time">{date} min ago</h3>
                    </div>
                </div>

            </div>
        </Link>
    )
}

export default TaskItem
